args:
// Custom Scrollbar
.custom-scrollbar {
  &::-webkit-scrollbar {
    width: 6px;
  }

  &::-webkit-scrollbar-track {
    background: transparent; 
  }

  &::-webkit-scrollbar-thumb {
    background-color: rgba($secondary, 0.3);
    border-radius: 20px;
    border: 2px solid transparent;
    background-clip: content-box;

    &:hover {
      background-color: rgba($secondary, 0.5);
    }
  }
}
